<?php include_once (TEMPLATEPATH . '/my_helpers.php'); ?><?php get_header(); ?>	<?php if (have_posts()) : ?>		<?php while (have_posts()) : the_post(); ?>			<div class="post">							<h3 id="post-<?php the_ID(); ?>">					<a href="<?php the_permalink() ?>" rel="bookmark" title="<?php the_title(); ?>">						<?php the_title(); ?>					</a>				</h3>								<p class="post-meta">					<span class="byline">						Written by <a href="<?php echo get_author_posts_url(get_the_author_ID()) ?>"><?php echo get_the_author() ?></a> on <span class="date"><?php the_time('m.d.y') ?></span>						<?php edit_post_link('Edit', ' ', ''); ?>					</span>									<span class="comments">						<a href="<?php comments_link(); ?>" title="Comments">							<?php comments_number('No Comments', '1 Comment', '% Comments'); ?>						</a>					</span>				</p>						<div class="entry-content">					<!--content with more link-->					<?php the_content('Continue reading ' . get_the_title()); ?>				</div>							</div>		<?php endwhile; ?>	<?php endif; ?>		</div><!-- /closes the #primary --><!--include sidebar--><?php get_sidebar();æ?><!--include footer--><?php get_footer(); ?>