<?php	include_once (TEMPLATEPATH . '/my_helpers.php'); ?><?php get_header(); ?>	<style type="text/css">		img.size-full {			width: 100%;			height: auto;		}	</style>			<?php		$cat = get_query_var('cat');		$current_category = get_category( $cat );	?>	<h3 class="category">		<?php echo $current_category->name . ' - ' . $current_category->category_count . ' posts'; ?>	</h3>		<!-- Main Content Area -->	<?php if (have_posts()) : the_post(); ?>	<div class="post">		<h3>			<a href="<?php the_permalink() ?>" rel="bookmark" title="<?php the_title(); ?>">				<?php the_title(); ?>			</a>		</h3>				<p class="post-meta">			<span class="byline">				Written by <a href="<?php echo get_author_posts_url(get_the_author_ID()) ?>"><?php echo get_the_author() ?></a> on <?php the_time('F jS, Y') ?>				<?php edit_post_link('Edit', ' ', ''); ?>			</span>					<span class="comments">				<a href="<?php comments_link(); ?>" title="Comments">					<?php comments_number('No Comments', '1 Comment', '% Comments'); ?>				</a>			</span>		</p>				<?php the_content('Continue reading ' . get_the_title()); ?>								</div>			<?php endif; ?>	<ul class="categories">			<?php			$query = 'category='.get_query_var('cat').'&numberposts=12&offset=1';						if($_GET['all']) {				$query = 'category='.get_query_var('cat').'&numberposts='.($current_category->category_count - 1).'&offset=1';			}						foreach(get_posts($query) as $post) : ?>			<li>				<a href="<?php echo get_permalink($post->ID); ?>">					<?php echo getFirstImage($post->post_content); ?>				</a>								<div class="footer">					<a href="<?php echo get_permalink($post->ID); ?>">						<span><?php echo truncate($post->post_title, 28) ?></span>					</a>					<div class="bg">&nbsp;</div>				</div>			</li>					<?php endforeach; ?>			</ul>		<?php if( $current_category->category_count > 13 && ! $_GET['all'] ) { ?>	<div class="all">		<a href="?all=1">... All ...</a>	</div>	<?php } ?>		</div><!--page.php end--><!--include sidebar--><?php include ('sidebar.php');æ?><!--include footer--><?php get_footer(); ?>