<?php // Do not delete these lines	if ('comments.php' == basename($_SERVER['SCRIPT_FILENAME']))		die ('Please do not load this page directly. Thanks!');	if (!empty($post->post_password)) { // if there's a password		if ($_COOKIE['wp-postpass_' . COOKIEHASH] != $post->post_password) {  // and it doesn't match the cookie?>			<p class="nocomments">This post is password protected. Enter the password to view comments.<p><?php			return;		}	}	/* This variable is for alternating comment background */	$oddcomment = 'alt';?><!-- You can start editing here. --><?php if (($comments) or ('open' == $post->comment_status)) : $shownavigation = 'yes'; ?>	<h3 id="comments">		<?php comments_number('No Responses', 'One Response', '% Responses' );?> to &#8220;<?php the_title(); ?>&#8221;	</h3> 		<div class="metalinks">		<span class="commentsrsslink">			<?php comments_rss_link('Feed for this Entry'); ?>		</span>		<?php if ('open' == $post->ping_status) { ?>			<span class="trackbacklink">				<a href="<?php trackback_url(); ?>" title="Copy this URI to trackback this entry">					Trackback Address				</a>			</span>		<?php } ?>	</div>		<div class="fb-like">				<fb:like action="like" colorscheme="light" font="arial" href="<?php echo get_permalink() ?>" show_faces="false" width="515"></fb:like>				<div id="fb-root"></div>		<script type="text/javascript">			window.fbAsyncInit = function() {				FB.init({appId: '113248718719389', status: true, cookie: true, xfbml: true});			};			(function() {				var e = document.createElement('script');				e.type = 'text/javascript';				e.src = document.location.protocol + '//connect.facebook.net/en_US/all.js';				e.async = true;				document.getElementById('fb-root').appendChild(e);			}());		</script>	</div>		<?php /* Seperate comments and pings */		if ( $post->comment_count > 0 ) {			$num_comments = 0;			$num_pings    = 0;			$comment_list = array();			$ping_list    = array();			foreach ($comments as $comment) {				if ( 'comment' == get_comment_type() ) {					$comment_list[++$num_comments] = $comment;				} else {					$ping_list[++$num_pings] = $comment;				}			}		}	?>	<ol class="commentlist">	<?php foreach ($comment_list as $comment) : ?>		<li class="<?php echo $oddcomment; ?>" id="comment-<?php comment_ID() ?>">			<?php if (function_exists('gravatar')) { ?>				<a href="http://www.gravatar.com/">					<img src="<?php gravatar("X", 32,  get_bloginfo('template_url')."/images/defaultgravatar.jpg"); ?>" class="gravatar" />				</a>			<?php } ?>			<span class="commentauthor"><?php comment_author_link() ?></span>			<?php if ($comment->comment_approved == '0') : ?>				<div class="comment-meta">					<em>Your comment is awaiting moderation.</em>				</div>			<?php endif; ?>			<p class="comment-meta">				<a href="#comment-<?php comment_ID() ?>" title="">					<?php comment_date('F jS, Y') ?> at <?php comment_time() ?>				</a>				<?php					if (function_exists('quoter_comment')) {						quoter_comment();					}				?>				<?php					if (function_exists('jal_edit_comment_link')) {						jal_edit_comment_link('Edit', '<span class="comment-edit">','</span>', '<em>(Editing)</em>');					}					else { 						edit_comment_link('Edit', '<span class="comment-edit">', '</span>');					}				?>			</p>			<?php comment_text() ?>		</li>		<?php /* Changes every other comment to a different class */				if ('alt' == $oddcomment) $oddcomment = '';			else $oddcomment = 'alt';		?>	<?php endforeach; /* end for each comment */ ?>	</ol> <?php else : // this is displayed if there are no comments so far ?>	<?php if ('open' == $post->comment_status) : ?> 		<!-- If comments are open, but there are no comments. -->	<?php else : // comments are closed ?>		<!-- If comments are closed. -->		<p>Comments are closed.</p>	<?php endif; ?>	<?php endif; ?><?php if ('open' == $post->comment_status) : ?>	<div id="makecomment">			<h3 id="respond" class="reply">			<?php if (isset($_GET['jal_edit_comments'])) { echo 'Edit Your Comment'; } else { echo 'Leave a Reply'; } ?>		</h3>			<?php if ( get_option('comment_registration') && !$user_ID ) : ?>			<p>You must be <a href="<?php echo get_option('siteurl'); ?>/wp-login.php?redirect_to=<?php the_permalink(); ?>">logged in</a> to post a comment.</p>		<?php else : ?>			<form action="<?php echo get_option('siteurl'); ?>/wp-comments-post.php" method="post" id="commentform">			<?php				if ( isset($_GET['jal_edit_comments']) ) {					$jal_comment = jal_edit_comment_init();					if (!$jal_comment) {						return;					}				} elseif ($user_ID) {			?>					<p class="comment-login">					<?php printf( 'Logged in as %s.', '<a href="'.get_option('siteurl').'/wp-admin/profile.php">'.$user_identity.'</a>'); ?>					<a class="logout" href="<?php echo get_option('siteurl'); ?>/wp-login.php?action=logout" title="<?php echo 'Log out of this account'; ?>">						<?php echo 'Logout'; ?> &raquo;					</a>				</p>							<?php } elseif ('' != $comment_author) { ?>				<p class="comment-welcomeback">					<?php printf( 'Welcome back <strong>%s</strong>', $comment_author); ?>					<span id="showinfo">(<a href="javascript:ShowUtils();"><?php _e('Change','k2_domain'); ?></a>)</span>					<span id="hideinfo">(<a href="javascript:HideUtils();"><?php _e('Close','k2_domain'); ?></a>)</span>				</p>			<?php } ?>				<ol class="comment-form-list">				<?php if ( !$user_ID ) : ?>					<li>						<input type="text" name="author" id="author" value="<?php echo $comment_author; ?>" />						<label for="author">Name <?php if ($req) echo "(required)"; ?></label>					</li>					<li>						<input type="text" name="email" id="email" value="<?php echo $comment_author_email; ?>" />						<label for="email">Mail <small>(will not be published) <?php if ($req) echo "(required)"; ?></small></label>					</li>					<li>						<input type="text" name="url" id="url" value="<?php echo $comment_author_url; ?>" />						<label for="url">Website</label>					</li>				<?php endif; ?>							<li>				<!--<p><small><strong>XHTML:</strong> You can use these tags: <?php echo allowed_tags(); ?></small></p>-->					<textarea name="comment" id="comment" rows="10"></textarea>					<span id="commenterror"></span>				</li>								<?php if (function_exists('show_subscription_checkbox')) { ?>					<li>						<?php show_subscription_checkbox(); ?>					</li>				<?php } ?>								<?php if (function_exists('quoter_page')) { ?>					<li>						<?php quoter_page(); ?>					</li>				<?php } ?>								<li>					<input name="submit" type="submit" id="submit" tabindex="5" value="Submit" />					<input type="hidden" name="comment_count" value="<?php echo $num_comments; ?>" />					<input type="hidden" name="comment_post_ID" value="<?php echo $id; ?>" />				</li>								<?php do_action('comment_form', $post->ID); ?>			</ol>		</form>	<?php endif; // If registration required and not logged in ?>	</div><!-- #makecomment --><?php endif; // if you delete this the sky will fall on your head ?>