<?php get_header(); ?>	<style type="text/css">		.post img.size-full {			width: 100%;			height: auto;		}	</style>	<?php if (have_posts()) : ?>				<?php if (is_search()) { ?>			<h2 class="searchquery">Search results for "<?php the_search_query() ?>"</h2>		<?php } ?>			<!--the loop-->		<?php while (have_posts()) : the_post(); ?>			<div class="post">				<h3>					<a href="<?php the_permalink() ?>" rel="bookmark" title="<?php the_title(); ?>">						<?php the_title(); ?>					</a>				</h3>				<p class="post-meta">					<span class="byline">						Written by <a href="<?php echo get_author_posts_url(get_the_author_ID()) ?>"><?php echo get_the_author() ?></a> on <?php the_time('F jS, Y') ?>						<?php edit_post_link('Edit', ' ', ''); ?>					</span>									<span class="comments">						<a href="<?php comments_link(); ?>" title="Comments">							<?php comments_number('No Comments', '1 Comment', '% Comments'); ?>						</a>					</span>				</p>					<div class="entry-content">					<?php the_content('Continue reading ' . get_the_title()); ?>				</div>							</div>		<?php endwhile; ?>				<ul id="breadcrumb">			<li class="prev"><?php next_posts_link('&laquo; Older Entries') ?></li>			<li class="next"><?php previous_posts_link('Newer Entries &raquo;') ?></li>		</ul>			<?php else : ?>			<?php if (is_search()) { ?>			<h2 class="searchquery">Sorry, no results for "<?php the_search_query() ?>"</h2>		<?php } ?>				<p>Oh no! You're looking for something which just isn't here! Fear not however, errors are to be expected, and luckily there are tools on the sidebar for you to use in your search for what you need.</p>			<?php endif; ?>	</div><!-- /closes the #primary --><!--include sidebar--><?php get_sidebar();æ?><!--include footer--><?php get_footer(); ?>