xbanner_sidebar .footer span').text( advertisements[i].title );		}				/**		 * Parse the advertisements that have been loaded		 */		for(var i=0; i <jQuery('#maxbanner_sidebar .store a').size(); i++) {			var $a = jQuery('#maxbanner_sidebar .store a').eq(i);						advertisements.push({				href: $a.attr('href'),				src: jQuery('img', $a).attr('src'),				title: jQuery('img', $a).attr('title')			});						if(i === 0) {				showAd(i);			} else {				$a.hide();			}		}				if(0 === jQuery('#maxbanner_sidebar .store a').size()) {			jQuery('#maxbanner_sidebar').hide();		}				jQuery('#maxbanner_sidebar .nav a').disableTextSelect();		jQuery('#maxbanner_sidebar .nav a.prev').bind('click', prevAd);		jQuery('#maxbanner_sidebar .nav a.next').bind('click', nextAd);				var advertisementIndex = 0;		var advertisementInterval = setInterval( function() {			advertisementIndex = (advertisementIndex + 1 ) % advertisements.length;			showAd( advertisementIndex );		}, 15000);	</script>	<div id="category_select" class="widget small">		<span>Categories:</span>		<select id="categories_select">			<option> - select here - </option>		<?php			$category_args = array('exclude'=> '578');			$categories= get_categories($category_args); 			foreach ($categories as $cat) {				$option = '<option value="/category/'.$cat->category_nicename.'">';				$option .= $cat->cat_name;				$option .= ' ('.$cat->category_count.')';				$option .= '</option>';				echo $option;			}		?>		</select>			<script type="text/javascript">			jQuery('#categories_select').change( function() {				window.location.href = '<?php bloginfo('url'); ?>' + jQuery(this).val();			});		</script>	</div>			<div id="follow_screen">			<ul class="categories">			<li>				<div class="header">					<span>Popular</span>					<div class="bg">&nbsp;</div>				</div>								<?php					$popular_posts = $wpdb->get_results("SELECT ID,post_title FROM {$wpdb->prefix}posts WHERE DATE_SUB(CURDATE(),INTERVAL 30 