<?php get_header(); ?>	<!--single.php-->	<!--loop-->				<?php if (have_posts()) : while (have_posts()) : the_post(); ?>		<!--post title-->		<div class="post">			<h3 id="post-<?php the_ID(); ?>">				<a href="<?php echo get_permalink() ?>" rel="bookmark" title="Permanent Link: <?php the_title(); ?>">					<?php the_title(); ?>				</a>			</h3>			<p class="post-meta">				<span class="byline">					Written by <a href="<?php echo get_author_posts_url(get_the_author_ID()) ?>"><?php echo get_the_author() ?></a> on <?php the_time('F jS, Y') ?>					<?php edit_post_link('Edit', ' ', ''); ?>				</span>							<span class="comments">					<a href="<?php comments_link(); ?>" title="Comments">						<?php comments_number('No Comments', '1 Comment', '% Comments'); ?>					</a>				</span>			</p>						<div class="entry-content">				<!--content with more link-->				<?php the_content('Continue reading ' . get_the_title()); ?>			</div>						<!--for paginate posts-->			<?php link_pages('<p><span>Pages:</span> ', '</p>', 'number'); ?>						<p class="after-post-meta">Filed under: <?php the_category(', ') ?>, and tagged <?php the_tags('', ', ',''); ?>.</p>				<!--all options over and out-->			<?php trackback_rdf(); ?>					<!--include comments template-->			<?php comments_template(); ?>		<!--do not delete-->		<?php endwhile; else: ?>			Sorry, no posts matched your criteria.		<!--do not delete-->		<?php endif; ?>			</div>	<ul id="breadcrumb">		<li class="prev"><?php previous_post_link('&laquo; %link') ?></li>		<li class="next"><?php next_post_link('%link &raquo;') ?></li>	</ul>	</div><!--single.php end--><!--include sidebar--><?php include ('sidebar.php');æ?><!--include footer--><?php get_footer(); ?>